/*wwwroot / js / interop.js*/

window.jsInterop = {
    setLocalStorage: (key, value) => localStorage.setItem(key, value),
    getLocalStorage: (key) => localStorage.getItem(key),
    removeLocalStorage: (key) => localStorage.removeItem(key)
};
































































































